import { NavLink, Outlet, useSearchParams } from "react-router-dom";
import { getSonglist } from "../data_songlists";

export default function Songlist() {
  let songlist = getSonglists();
  let [searchParams, setSearchParams] = useSearchParams();

  return (
    <main
      style={{ padding: "1rem" }}
      className="flex-grow px-4 py-8 w-full prose antialiased ml-0 sm:ml-56 mt-0 sm:mt-12"
    >
      <p>
        <span class="font-bold">Songlist</span>

        {songlist
          .filter((songlist) => {
            let filter = searchParams.get("filter");
            if (!filter) return true;
            let name = songlist.name.toLowerCase();
            return name.startsWith(filter.toLowerCase());
          })
          .map((songlist) => (
            <NavLink
              style={({ isActive }) => ({
                display: "block",
                margin: "1rem 0",
                color: isActive ? "red" : "",
              })}
              to={`/shows/1`}
              key={songlist.id}
            ></NavLink>
          ))}
      </p>
    </main>
  );
}
