let playlists = [
  {
    id: "104",
    playlist: ["hi", "bye", "whatever"],
  },
];
export function getPlaylists() {
  return playlists;
}

export function getPlaylist(id) {
  return playlists.find((playlist) => playlist.id === id);
}
