        .filter((song) => {
          let filter = searchParams.get("filter");
          if (!filter) return true;
          let name = song.name.toLowerCase();
          return name.startsWith(filter.toLowerCase());
        })
        .map((song) => (
          <NavLink
            style={({ isActive }) => ({
              display: "block",
              margin: "1rem 0",
              color: isActive ? "red" : "",
            })}
            to={`/songs/${song.id}`}
            key={song.id}
          >
            {song.name}
          </NavLink>
        ))}

      <Outlet />
    </div>
  );
}
